/**
 * Created by ovesh on 25/8/20.
 */
import React from 'react';
import {useState , useEffect } from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from '@material-ui/core/Paper'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/es/Typography/Typography";
import AppBar from '@material-ui/core/AppBar';
import fire from 'firebase';
import Button from "@material-ui/core/Button";
import history from './history';
const useStyles = makeStyles ((theme) => ({
    card:{
        width: 'auto',
        minWidth: 120,
        margin: 10

    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
        color: theme.palette.common.black,
    },
    appBar:{
        padding: 10,
        backgroundColor: theme.palette.common.white,
        display:"flex",
        flexDirection: 'row'
    },
    mainArea:{
        marginTop:theme.spacing(10),
    },
    signOut:{
        border:'1px solid grey',
    },
    grow: {
        flexGrow: 1,
    },

}));


function Home(){
const  classes = useStyles();
    const [data , setData] = useState([]);



    const fetchData = async () => {
       const Country =  await  fetch("https://gateway.ayoblajar.com/services/user-service/api/system/country/findAll/default");

       const countryData = await Country.json();
       setData(data =>  data = countryData);

       const songs = await fetch("https://spotify-api-wrapper.appspot.com/artist/bruno");
       const songData =  await songs.json();
       console.log(songData.artist);
    };


const signOut =() => {
    fire.auth().signOut();
    localStorage.clear();
    history.push("/");

};


console.log(data);

    useEffect( () => {
        fetchData();
    },[]);

    return (
        <div>
            <AppBar className={classes.appBar}>
                <Grid  container
                       direction="row"
                       alignItems="center"
                >
                    <Typography variant="h4" color="inherit" className={classes.title}>
                        Musix
                    </Typography>
                    <div className={classes.grow} />
                    <Button variant="outlined" color="secondary" onClick={signOut}>
                        Sign Out
                    </Button>
                </Grid>

            </AppBar>
            <div className={classes.mainArea}>
                <Grid
                    container
                    direction="row"
                    justify="flex-start"
                    alignItems="flex-start"
                >
                    {
                        data.map( val => (
                            <Grid elevation={3} className={classes.card} key={val.id} item xs={12} md={4}>
                                <Paper>
                                    <Typography align={'center'}>
                                        {val.name}
                                    </Typography>
                                    <div align={'center'}>Time Zone :{val.timezoneId}</div>
                                    <div align={'center'}>IsoCode : {val.isoCode}</div>
                                </Paper>
                            </Grid>
                        ))
                    }

                </Grid>
            </div>
        </div>
    );
}


export default Home;
