import React from "react";
import image from "../assets/1.jpg";
import Grid from "@material-ui/core/es/Grid/Grid";
import Paper from "@material-ui/core/es/Paper/Paper";
import Typography from "@material-ui/core/es/Typography/Typography";
import Button from "@material-ui/core/es/Button/Button";
import {useState} from 'react';
function App() {

    return (
        <div>
            <div
                style={{
                    backgroundImage: "url(" + image + ")",
                    backgroundSize: "cover",
                    opacity: 0.8,
                    filter: "grayscale(80%)",
                    backgroundPosition: "top center",
                    minHeight: "98vh",
                    height: "auto"
                }}
            >
                <Grid direction="row" justify="flex-end" alignItems="center" container>
                    <Grid
                        style={{
                            margin: "100px 30px"
                        }}
                        item
                        xs={12}
                        sm={12}
                        md={6}
                    >
                        <Paper
                            elevation={3}
                            style={{
                                minHeight: 90,
                                width: "70%",
                                margin: "0px 15%",
                                padding: "20px 10px",
                                position: "relative",
                                top: 50,
                                textAlign: "center"
                            }}
                        >
                            <Typography>We welcome you</Typography>
                            <h1>
                                Musix
                            </h1>
                            <div>
                                <Button
                                    href="#pablo"
                                    target="_blank"
                                    onClick={(e) => e.preventDefault()}
                                >
                                    <i className={"fab fa-twitter"} />
                                </Button>
                                <Button
                                    href="#pablo"
                                    target="_blank"
                                    onClick={(e) => e.preventDefault()}
                                >
                                    <i className={"fab fa-facebook"} />
                                </Button>
                                <Button
                                    href="#pablo"
                                    target="_blank"
                                    onClick={(e) => e.preventDefault()}
                                >
                                    <i className={"fab fa-google-plus-g"} />
                                </Button>
                            </div>
                        </Paper>
                        <Paper
                            elevation={3}
                            style={{
                                minHeight: "50vh",
                                minWidth: "50%"
                            }}
                        >
                            <div  style={{
                                padding: "100px 0px"
                            }}>
                                <Form/>
                            </div>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}

function Form(props) {
    const [ user , UserState] = useState({username:'',password:''});
    const {username , password} = user;

    const update = (event) => {
        const {name , value} = event.target;


        UserState(
            data => ({
                ...data,[name]:value
            })
        );

    };

     const login = ()=> {

         alert(password);
     };


    return (
        <div>
            <Typography align={'center'}>
                Or Go Basics
            </Typography>
            <hr width={'60%'}/>
            <Grid direction="row" justify="center" alignItems="center" container>
                <form onSubmit={login} >
                     <input type="text"
                            name="username"
                            value={username}
                            onChange={update}
                            style={{padding: "10px", margin:20, width: "100%",}}
                            placeholder="Enter UserName"/>

                     <input type="password"
                            name="password"
                            value={password}
                            onChange={update}
                            style={{padding: "10px", margin:20, width: "100%",}}
                            placeholder="Enter Password"/>

                     <input type="submit"
                            style={{padding: "10px", margin:20, width: "100%",}}/>
                 </form>
            </Grid>
        </div>
    );

}

export default App;
