/**
 * Created by ovesh on 25/8/20.
 */
import React from 'react';
import {useState , useEffect } from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from '@material-ui/core/Paper'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/es/Typography/Typography";
import AppBar from '@material-ui/core/AppBar';

const useStyles = makeStyles ((theme) => ({
    card:{
        width: 'auto',
        padding: theme.spacing(3),
        margin: theme.spacing(2),
    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    appBar:{
        padding: theme.spacing(3),
        backgroundColor: theme.palette.common.black ,
    }
}));


function Home(){
const  classes = useStyles();
    const [data , setData] = useState([]);



    const fetchData = async () => {
       const Country =  await  fetch("https://gateway.ayoblajar.com/services/user-service/api/system/country/findAll/default");

       const countryData = await Country.json();
       setData(data =>  data = countryData);
    };





console.log(data);

    useEffect( () => {
        fetchData();
    },[]);

    return (
        <div>
            <AppBar className={classes.appBar}>
                <Typography className={classes.title}>
                    Musix
                </Typography>
            </AppBar>
            <div>
                <Grid
                    container
                    direction="row"
                    justify="flex-start"
                    alignItems="flex-start"
                >
                    {
                        data.map( val => (
                            <Paper elevation={3} className={classes.card} key={val.id}>
                                <Typography align={'center'}>
                                    {val.name}
                                </Typography>
                                <div>Time Zone :{val.timezoneId}</div>
                                <div>IsoCode : {val.isoCode}</div>
                            </Paper>
                        ))
                    }
                </Grid>
            </div>

        </div>
    );
}


export default Home;
