/**
 * Created by ovesh on 25/8/20.
 */
import React from 'react';
import {useState , useEffect } from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from '@material-ui/core/Paper'
import {makeStyles} from "@material-ui/core/styles";


const useStyles = makeStyles ((theme) => ({
    card:{
        width: 100,
        padding: theme.spacing(3),
    }
}));



function Home(){
const  classes = useStyles();
    const [data , setData] = useState([]);
    const fetchData = async () => {
       const Country =  await  fetch("https://gateway.ayoblajar.com/services/user-service/api/system/country/findAll/default");
       const countryData = await Country.json();
       setData(data =>  data = countryData);
    };

    useEffect( () => {
        fetchData();
    },[]);

    return (
        <div>
            <Grid
                container
                direction="row"
                justify="flex-start"
                alignItems="flex-start"
            >
                {
                    data.map( val => (
                        <Paper elevation={3} className={classes.card} key={val.id}>{val.name}</Paper>
                    ))
                }
            </Grid>
        </div>
    );
}


export default Home;
