/**
 * Created by ovesh on 25/8/20.
 */
import React from 'react';
import {useState , useEffect } from 'react';
import Grid from "@material-ui/core/Grid";
import Paper from '@material-ui/core/Paper'
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/es/Typography/Typography";
import AppBar from '@material-ui/core/AppBar';
import fire from 'firebase';
import {Button} from "@material-ui/core/es/index";

const useStyles = makeStyles ((theme) => ({
    card:{
        width: 'auto',
        minWidth: 120,
        margin: 30

    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    appBar:{
        padding: 10,
        backgroundColor: theme.palette.common.black ,
    },
    mainArea:{
        marginTop:theme.spacing(3),
    }
}));


function Home(){
const  classes = useStyles();
    const [data , setData] = useState([]);



    const fetchData = async () => {
       const Country =  await  fetch("https://gateway.ayoblajar.com/services/user-service/api/system/country/findAll/default");

       const countryData = await Country.json();
       setData(data =>  data = countryData);
    };


const signOut =() => {
    fire.auth().signOut();
    localStorage.clear();
};


console.log(data);

    useEffect( () => {
        fetchData();
    },[]);

    return (
        <div>
            <AppBar className={classes.appBar}>
                <Typography className={classes.title}>
                    Musix
                </Typography>
                <Button onClick={signOut}>
                    Sign Out
                </Button>
            </AppBar>
            <div className={classes.mainArea}>
                <Grid
                    container
                    direction="row"
                    justify="flex-start"
                    alignItems="flex-start"
                >
                    {
                        data.map( val => (
                            <Grid elevation={3} className={classes.card} key={val.id} item xs={12} md={4}>
                                <Paper>
                                    <Typography align={'center'}>
                                        {val.name}
                                    </Typography>
                                    <div align={'center'}>Time Zone :{val.timezoneId}</div>
                                    <div align={'center'}>IsoCode : {val.isoCode}</div>
                                </Paper>
                            </Grid>
                        ))
                    }

                </Grid>
            </div>
        </div>
    );
}


export default Home;
